  mutate(SCOPA_DIG=SCOPA1+SCOPA2+SCOPA3+SCOPA4+SCOPA5+SCOPA6+SCOPA7) %>%
  mutate(SCOPA_URI=SCOPA8+SCOPA9+SCOPA10+SCOPA11+SCOPA12+SCOPA13) %>%
  mutate(SCOPA_CDV=SCOPA14+SCOPA15+SCOPA16) %>%
