  mutate(SCOPA_PUP=SCOPA21) %>%
  mutate(SCOPA_SEX=SCOPA22H+SCOPA22F+SCOPA23F+SCOPA23H) %>%
  mutate(SCOPA_TOT=SCOPA_SEX+SCOPA_PUP+SCOPA_THE+SCOPA_CDV+SCOPA_URI+SCOPA_DIG)

