SCOPA <- SCOPA %>% filter(SCOPA_TOT!=0) 

length(unique(SCOPA$NUM))


SCOPA_Baseline_Pats <- SCOPA %>% filter(Year==0) %>% filter(!is.na(SCOPA_TOT)) %>%
